export const invertColor = (hex) => {
    if (hex.indexOf('#') === 0) {
      hex = hex.slice(1);
    }
  
    const color = parseInt("0x" + hex);

    const invertedHex = 0xffffff ^ color
  
    return invertedHex;
  }
